import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Login.module.css';
import 'animate.css';

const Login = ({ onLogin }) => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    useEffect(() => {
        async function handleAuthorization() {
            const path = window.location.pathname;
            if (path.includes('/authorization/')) {
                const userId = path.split('/authorization/')[1];
                try {
                    const response = await fetch(`http://localhost:5000/authorization/${userId}`);
                    const data = await response.json();
                    if (data.message === 'User authorized successfully') {
                        onLogin(data.user.email);
                    }
                } catch (error) {
                    console.error('Error handling authorization:', error);
                }
            }
        }
        handleAuthorization();
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();

        //API sending request
        try {
            const response = await fetch('http://localhost:5000/api/users/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ email, password }),
            });

            const data = await response.json();

            //API handle responses
            if (response.ok) {
                // Handle successful login
                console.log('Login successful:', data);
                onLogin(email);
            } else {
                // Handle unsuccessful login
                console.error('Login failed:', data);
            }
        } catch (error) {
            // Handle network errors
            console.error('Network error:', error);
        }
    };


    return (
        <div className="login-background">
            <a className="Link" href="/">Go to Home Page</a>
            <div className="login-container">
                <h1 className='login-h1'>Login</h1>

                <form className="login-form" onSubmit={handleSubmit}>
                    <input
                        type="email"
                        placeholder="Email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                    <input
                        type="password"
                        placeholder="Password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <button type="submit">Let me in!</button>
                    <Link to="/registration">Create an account</Link>
                </form>
            </div>
        </div>
    );
};

export default Login;
