import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';

import Login from './components/Login/Login';
import LoggedInScreen from './components/LoggedInScreen/LoggedInScreen';
import NonLoggedInScreen from './components/NonLoggedInScreen/NonLoggedInScreen';
import CardDetails from "./components/CardDetails/CardDetails";
import Registration from './components/Registration/Registration';


function App() {
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [userEmail, setUserEmail] = useState('');

    useEffect(() => {
        async function handleAuthorization() {
            const path = window.location.pathname;
            if (path.includes('/authorization/')) {
                const userId = path.split('/authorization/')[1];
                try {
                    const response = await fetch(`http://localhost:5000/authorization/${userId}`);
                    const data = await response.json();
                    if (data.message === 'User authorized successfully') {
                        localStorage.setItem('isLoggedIn', true);
                        localStorage.setItem('userEmail', data.user.email);
                        history.push('/logged-in');
                    } else {
                        history.push('/login');
                    }
                } catch (error) {
                    console.error('Error handling authorization:', error);
                    history.push('/login');
                }
            }
        }
        handleAuthorization();
    }, [history]);


    const handleLogout = () => {
        setIsAuthenticated(false);
    };

    return (
        <Router>

            <Routes>
                <Route path="/" element={<NonLoggedInScreen />} />
                <Route
                    path="/login"
                    element={
                        isAuthenticated ? (
                            <Navigate to="/logged-in" />
                        ) : (
                            <Login onLogin={(email) => {
                                setIsAuthenticated(true);
                                setUserEmail(email);
                            }} />
                        )
                    }
                />
                <Route
                    path="/logged-in"
                    element={
                        isAuthenticated ? (
                            <LoggedInScreen userEmail={userEmail} onLogout={handleLogout}/>
                        ) : (
                            <Navigate to="/login" />
                        )
                    }
                />
                <Route path="/card/:id"
                       element={<CardDetails />} />

                <Route path="/registration" element={<Registration />} />

            </Routes>

        </Router>
    );
}
export default App;
